objectDef.script:


getEntry = ?{building:
    if(find(?{entry: entry.tmplCityBuilding == building}, self.objCityBuildingDisplayEntries)==null,
        self.objCityBuildingDisplayEntries.getArbitraryValue(),
        find(?{entry: entry.tmplCityBuilding == building}, self.objCityBuildingDisplayEntries)
    )
}



        getEntry = ?{unit:
				?{foundEntry:
					if(foundEntry != null, foundEntry, self.objDefaultEntry)
				}(find(?{entry: entry.tmplUnit == unit}, self.objUnitDisplayEntries))
			}
